from math import inf

CardData=[11,12,25,33,52,56,57,59,85,91]


def binsea(x,CardData):
    if not CardData:
        return inf
    mid = len(CardData)//2
    if x == CardData[mid]:
        return mid
    elif x < CardData[mid]:
        return binsea(x,CardData[:mid])
    else:
        return mid + 1 + binsea(x,CardData[mid+1:])


