#!/usr/bin/env perl
use strict;
use warnings;
use Data::Dumper;


my $packed_output = pack "L>*", 1, 2, 3, 4, 5, 6;
my $hex = sprintf "%v02X", $packed_output;
# $hex is
# 00.00.00.01.00.00.00.02.00.00.00.03.00.00.00.04.00.00.00.05.00.00.00.06

my $binary = pack "(h2)*", map(hex, split(/\./, $hex));

