function sortByAs(arr) {
  return arr.sort((a, b) => {
    const diff =  numberOfAs(b) - numberOfAs(a);
    // different #s of A, return the comparison
    if (diff !== 0) return diff;
    // fallback to length comparison
    return b.length - a.length;
  });
